package Basic_Array;public class Contain {    public static void main(String[]args){        int arr[]={2,3,4,5};        int contain=8;        int size=arr.length;        int check=1;        for (int i=0;i<size;i++){            if(arr[i]==contain){               check=0;            }        }        if(check==0){            System.out.println(contain+"it is present");        }else{            System.out.println(contain+" it is not present");        }    }}