package Basic_Array;public class FindMin_max {    public static void main(String[] args) {        int arr[] = {2, 3, 4, 5, 1, 6, 3, 9, 8};        int size = arr.length;        int max = arr[0];        int min = arr[0];        for (int i = 0; i < size; i++) {            if (arr[i] > max) {                max = arr[i];            }        if (arr[i] < min) {            min = arr[i];        }    }        System.out.println("it is max value-"+max);        System.out.println("it is min value-"+min);    }}