package Basic_ClassCode;public class BankAccount {   private int accNo;   private int balance;    static String bankName;    static double intrest;static {    bankName="KOTAK";    intrest=6.7;}  public BankAccount(){      accNo=123;      balance=2000;  }  public BankAccount(int accNo,int balance){    this.accNo=accNo;    this.balance=balance;  }  public String toString(){    return "BANK- "+bankName+"\n"+"accNo- "+accNo+" balance-  "+balance+"rs"+" intrest - "+intrest+"%";  }}