package Basic_ClassCode;public class Dog {    private String bride;    private int dogAge;    private String color;    public Dog(){        bride="labra";        dogAge=1;        color="brown";    }    public Dog(String bride,int dogAge,String color){        this.bride=bride;        this.dogAge=dogAge;        this.color=color;    }    public void setBride(String bride) {        this.bride = bride;    }    public String getBride() {        return bride;    }    public void setDogAge(int dogAge) {        this.dogAge = dogAge;    }    public int getDogAge() {        return dogAge;    }    public void setColor(String color) {        this.color = color;    }    public String getColor() {        return color;    }    public void print(){        System.out.println("Dog breed- "+bride+"Dog Age- "+dogAge+"Dog Color- "+color);    }}