package Basic_ClassCode;public class Rectangle {    float height;    float width;    float side;    public  Rectangle(){       height=12;       width=10;        side=4;    }    public Rectangle(float height,float width,float side){        this.height=height;        this.width=width;        this.side=side;    }    public  void caluate(float height,float width,float side){        System.out.println("perimeter or triangle-"+(height+width+side));    }    public  void caluate(float he,float wi){        System.out.println("Area or triangle-"+((0.5)*(wi*he)));    }}