package Basic_ClassCode;public class TrafficLight {    String color;    int duration;    public TrafficLight() {        color = "red";        duration = 4;    }    public TrafficLight(String color, int duration) {        this.color = color;        this.duration = duration;    }    public int getDuration() {        return duration;    }    public String getColor() {        return color;    }    public void setColor(String color) {        this.color = color;    }    public void setDuration(int duration) {        this.duration = duration;    }    public boolean redCheck() {        return color==("red");    }    public boolean greenCheck() {        return color==("green");    }}