package Basic_ClassCode;public class TrafficLightMain {    public static void main(String[] args) {        TrafficLight tl = new TrafficLight("red", 7);        tl.setDuration(3);        tl.setColor("red");        if (tl.redCheck()) {            System.out.println("it is "+tl.getColor() + " traffic light and " + tl.getDuration() + " duration");            System.out.println("check its red");        }        else if(tl.greenCheck()){            System.out.println("it is "+tl.getColor() + " traffic light and " + tl.getDuration() + " duration");            System.out.println("check its green ");        }        else{            System.out.println(" ");        }    }}