package Basic_JavaCode;public class AdditionOdd_even {    public static void main (String[]args) {        int num = 20;        int oddSum=0;        int evenSum=0;        for (int i = 0; i < num; i++) {            if (i % 2 == 0) {                oddSum=oddSum+i;            } else if(i % 2 != 0){               evenSum=evenSum+i;            }        }        System.out.println("Total sum of odd num is "+oddSum);        System.out.println("Total sum of even num is "+evenSum);    }}