package Basic_JavaCode;public class Amstrong {    public static void main(String[] args) {        int num = 153;        int sum = 0;        int temp=num;        while (num != 0) {          int total= num % 10;         sum=sum+total*total*total;            num = num / 10;        }        if(temp==sum){            System.out.println(temp+" it is a amstrong");        }else{            System.out.println(temp+" it is not amstrong");        }    }}