package Basic_JavaCode;public class CheckVal {    public static void main(String[]args){        char value='*';        if((value>=65 && value<=90) ||(value>=97&&value<=122)){            System.out.println(value+" it is a letter");        }else if(value >=48&&value<=57){            System.out.println(value+" it is a number");        }else{            System.out.println(value+" it is a special character");        }    }}