package Basic_JavaCode;public class MenuDrivenXD {    public static void main (String[]args){        int num1=10;        int num2=20;        System.out.println("choose no");        System.out.println("1.addtion");        System.out.println("2.substraction");        System.out.println("3.multiplication");        System.out.println("4.division");        int choose=5;        System.out.println("you choose "+choose+" option");        switch (choose){            case 1:                System.out.println(num1+num2+" total addition");                break;            case 2:                System.out.println(num1-num2+" total substration");                break;            case 3:                System.out.println(num1*num2+" total multiplication");                break;            case 4:                System.out.println(num1/num2+" total division");                break;            default:                System.out.println("out off option");        }    }}