package Basic_JavaCode;public class OppoCase {    public static void main(String[] args) {        char value = 'p';        if (value >= 65 && value <= 90 ) {       int oppo = value+32;       char ans= (char) oppo;            System.out.println(ans);        }else if(value>=97&&value<=122){            int oppo = value-32;            char ans= (char) oppo;            System.out.println(ans);        }        else{            System.out.println(value+" it is not letter");        }    }}