package Collection.Account;import java.util.ArrayList;import java.util.Iterator;import java.util.Scanner;public class BankMain {    public static void main(String[] args) {        Account acc1=new Account(10001,"Suraj",3000.0);        Account acc2=new Account(10011,"raj",3000.0);        Account acc3=new Account(10021,"arjun",3000.0);        Account acc4=new Account(10031,"Raju",3000.0);        ArrayList arr=new ArrayList();        arr.add(acc1);        arr.add(acc2);        arr.add(acc3);        arr.add(acc4);        Iterator it= arr.iterator();         int num=1;        while (it.hasNext()){            System.out.println(num+". "+(it.next()));            System.out.println("---------");            num++;        }    }}