package Collection.Collection_tYPE;import java.util.Hashtable;public class Collection_HashTable {    public static void main(String[] args) {        Hashtable ht=new Hashtable();        ht.put(101,"anuj");        ht.put(102,"sarvesh");        ht.put(103,"tejash");        ht.put(104,"aditya");        ht.put(105,"yash");        System.out.println(ht);        ht.remove(105);        System.out.println(ht);        System.out.println(ht.size());        System.out.println(ht.values());        System.out.println(ht.keySet());        System.out.println(ht.get(104));        System.out.println(ht.containsKey(103));        System.out.println(ht.containsValue("anuj"));        ht.replace(101,"abhishek");        System.out.println(ht);    }}