package Collection.Employee_System;import java.util.ArrayList;import java.util.Scanner;public class AddEmployee {    public static void add(ArrayList l) {        Scanner sc = new Scanner(System.in);        System.out.println("Employee ID- ");        int id = sc.nextInt();        sc.nextLine();        System.out.println("Employee Name- ");        String name = sc.nextLine();        System.out.println("Employee Salary- ");        double salary = sc.nextDouble();        l.add(new Employee(id, name, salary));        System.out.println("Employee add :)");    }}