package Collection.Employee_System;public class Employee {    int empID;    String empName;    double salary;    public Employee(){        empID=100;        empName="Aditya";        salary=500;    }    public Employee(int id,String nm,double sal){        this.empID=id;        this.empName=nm;        this.salary=sal;    }    @Override    public String toString() {        return "Employee ID- "+empID+"\nEmployee Name- "+empName+"\nSalary- "+salary;    }}