package Collection.Employee_System;import java.util.ArrayList;import java.util.Scanner;public class EmployeeMain {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        ArrayList empXD=new ArrayList();boolean repeted=true;        while (repeted) {            System.out.println("Choose an option:");            System.out.println("1 Add Employee");            System.out.println("2 Remove Employee");            System.out.println("3 Search Employee");            System.out.println("4 Display Employees");            System.out.println("5 Exit");            int choice = sc.nextInt();            sc.nextLine();            switch (choice) {                case 1:                    System.out.println("Employee ID- ");                    int id = sc.nextInt();                    sc.nextLine();                    System.out.println("Employee Name- ");                    String name = sc.nextLine();                    System.out.println("Employee Salary- ");                    double salary = sc.nextDouble();                    empXD.add(new Employee(id, name, salary));                    System.out.println("Employee add :)");                    break;                case 2:                    System.out.println("Enter Employee remove Id- ");                    int removeId = sc.nextInt();                    boolean flag = false;                    for(Object d:empXD) {                        Employee emp = (Employee) d;                        if (removeId == emp.empID) {                            flag = true;                            empXD.remove(emp);                            System.out.println("Employee Remove!");                        }                        break;                    }                case 3:                    System.out.println("Enter Employee search Id- ");                    int searchId = sc.nextInt();                    boolean flag1 = false;                    for(Object d:empXD) {                        Employee s = (Employee) d;                        if(searchId == s.empID) {                            flag1 = true;                            System.out.println("Employee Found Successfully!!!");                            break;                        }                    }                    if(flag1 == false) {                        System.out.println("Employee Not Exist!!");                    }                    break;                case 4:                    System.out.println("Display employee-");                        System.out.println(empXD);                    break;                case 5:                     repeted=false;                    System.out.println("you are exit||");                    break;            }        }        }}