package Collection.Employee_System;import ExceptionHandling.Employee;import java.util.ArrayList;import java.util.Scanner;public class EmployeeMain {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        ArrayList empXD=new ArrayList();boolean repeted=true;        while (repeted) {            System.out.println("Choose an option:");            System.out.println("1 Add Employee");            System.out.println("2 RemoveEmployee Employee");            System.out.println("3 Search Employee");            System.out.println("4 Display Employees");            System.out.println("5 Exit");            int choice = sc.nextInt();            sc.nextLine();            switch (choice) {                case 1:                  AddEmployee.add(empXD);                    break;                case 2:                  RemoveEmployee.remove(empXD);                        break;                case 3:                 SearchEmployee.search(empXD);                    break;                case 4:                    EmployeeDisplay.display(empXD);                    break;                case 5:                    ExitProgram.exit(repeted);                    break;            }        }        }}