package Collection.Employee_System;import java.util.ArrayList;import java.util.Scanner;public class SearchEmployee {    public static void search(ArrayList l){        Scanner sc = new Scanner(System.in);        System.out.println("Enter Employee search Id- ");        int searchId = sc.nextInt();        boolean flag1 = false;        for(Object d:l) {            Employee s = (Employee) d;            if(searchId == s.empID) {                flag1 = true;                System.out.println("Employee Found Successfully!!!");                break;            }        }        if(flag1 == false) {            System.out.println("Employee Not Exist!!");        }    }}