package Constructor;public class Chair {    private  String color;    private String chairType;    private int chairNo;    private boolean foaldable;    public Chair() {         color="Blue";            chairType="plastic";            chairNo=16;            foaldable=false;    }    public void setColor(String c) {        color = c;    }    public String getColor() {        return color;    }    public void setChairType(String ct) {        chairType = ct;    }    public String getChairType() {        return chairType;    }    public void setChairNo(int cs) {        chairNo = cs;    }    public int getChairNo() {        return chairNo;    }    public void setFoaldable(boolean f) {        foaldable = f;    }    public boolean getFoaldable() {        return foaldable;    }    void printDetail(){        System.out.println("color : "+color+"... chairType : "+chairType+"chairNo : "+chairNo+"... foaldable : "+foaldable);    }}