package Contractor;public class Customer {    private int customerId;    private  String custname;    private String city;    public Customer() {        customerId = 100;        custname = "Anuj";        city = "Amravati";    }    public void print() {        System.out.println("Customer Id: " + customerId);        System.out.println("Customer Name: " + custname);        System.out.println("Customer City: " + city);    }}