package Contractor;public class MobileDevice {    private  String company;    private int modelNo;    private String modelName;    private double price;    public MobileDevice(){        company="iPhone";        modelNo=20110;        modelName="iPhone16";        price=50000.09;    }    public void setCompany(String c) {        company = c;    }    public void setModelNo(int m) {        modelNo = m;    }    public void setModelName(String mn) {        modelName = mn;    }    public void setPrice(double p) {        price = p;    }    public String getCompany() {        return company;    }    public int getModelNo() {        return modelNo;    }    public String getModelName() {        return modelName;    }    public double getPrice() {        return price;    }    void printDetail(){        System.out.println("Company : "+company+"... ModelName : "+modelName+"ModelNo : "+modelNo+"... Price : "+price);    }}