package Employee_Information.AllEmployee;import Employee_Information.Person.Address;import Employee_Information.Person.Date;public class Manager extends Employee {    int noofsubordinate;    int incentive;    int totalAmmount;public  Manager(){    noofsubordinate=0;    incentive=8;} public Manager(String name, Date dob, Address add, double bSal, int noofsubordinate, int incentive){        super(name,dob,add,bSal);     this.noofsubordinate=noofsubordinate;     this.incentive=incentive; }  public int calculateIncentive(){        return incentive*noofsubordinate;  }   public void calculateSalary(){       totalAmmount=incentive*noofsubordinate;       System.out.println("Mnager total incentive- "+totalAmmount);   }    @Override    public String toString() {        return super.toString()+"\nThis Employee is a Manager"+"\nNo of subordinate "+noofsubordinate+"\nincentive-"+calculateIncentive();    }}