package Employee_Information.Performance;import Employee_Information.AllEmployee.*;public  class Accept {    public static void accept(Employee emp){            if (emp instanceof WageEmployee) {                System.out.println("\n");                WageEmployee wag = (WageEmployee)emp;                System.out.println(wag);                System.out.println("\n");            }            else if (emp instanceof SaleEmployee) {                SaleEmployee sal=(SaleEmployee)emp;                System.out.println(sal);                System.out.println("\n");            }            else if (emp instanceof Manager) {                Manager mag=(Manager)emp;                System.out.println(mag);                System.out.println("\n");            }            else if (emp instanceof Developer) {                Developer dev=(Developer)emp ;                System.out.println(dev);                System.out.println("\n");            }        }    }