package Employee_Information.Person;public class Address {    String cityName;    int zipCode;   public Address(){       cityName="Amravti";       zipCode=444606;   }   public Address(String cityName,int zipCode){       this.cityName=cityName;       this.zipCode=zipCode;   }    @Override    public String toString() {        return "City Name- "+cityName+" Zip Code- "+zipCode;    }}