package Employee_Information.Person;public class Date {    int day;    int month;    int year;    public Date(){        day=12;        month=7;        year=2022;    }    public Date(int day,int month,int year){        this.day=day;        this.month=month;        this.year=year;    }    @Override    public String toString() {        return day+"/"+month+"/"+year;    }}