package Employee_Information_Sat.Employee_Information.AllEmployee;import Employee_Information_Sat.Employee_Information.Person.Address;import Employee_Information_Sat.Employee_Information.Person.Date;import Employee_Information_Sat.Employee_Information.Person.Person;public class Employee extends Person {    private int empId;    private double bSal;    private double hra;    private double medical;    private double pf;    private double pt;    static        int count = 100;    public Employee() {        super();        this.empId = 101;        this.bSal = 110000.0;        hra = 90000.0;        pf = 80000.0;        pt = 200.0;    }    public Employee(String name, Date dob, Address add , double bSal) {        super(name,dob,add);        count++;        this.empId = count;        this.bSal = bSal;    }    private void calculateSalary() {        hra = 0.5 * bSal;        pf = 0.12 * bSal;    }    public String toString() {        return super.toString()+"\nEmp Id- "+empId+"\n Basic Salary- "+bSal ;    }}