package Employee_Information_Sat.Employee_Information.AllEmployee;import Employee_Information_Sat.Employee_Information.Person.Address;import Employee_Information_Sat.Employee_Information.Person.Date;public class SaleEmployee extends WageEmployee {    int sale;    double commision;    public SaleEmployee(){        super();        sale=1;        commision=0.5;    }    public SaleEmployee(String name, Date dob, Address add , int hours, int rate, double bSal, int sale, double commision){        super(name,dob,add,hours, rate, bSal);        this.sale=sale;        this.commision=commision;    }    public float calculatCommission(){        return (float) (commision*sale);    }    @Override    public String toString() {        return super.toString()+"\nTotal commision is- "+calculatCommission();    }}