package Employee_Information_Sat.Employee_Information.Person;public class Person {    String name;    Date dateofBirthDay;    Address address;    public Person(){        name="Anuj";        dateofBirthDay=new Date();        address =new Address();    }    public  Person(String name, Date dateofBirthDay, Address address){        this.name=name;        this.dateofBirthDay=new Date();        this.address=new Address();    }    @Override    public String toString() {        return "Name-"+name+" Date of birth- "+dateofBirthDay+" Address-"+address;    }}