package ExceptionHandling.ExceptionOdd;import java.util.Scanner;public class Odd {    public static void Check(int n)throws ArithmeticException{        if(n%2!=0)            throw new ArithmeticException("This is odd no Exception");        System.out.println("this is notmal even no");}    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.println("Enter the no- ");        int num = sc.nextInt();        try        {            Check(num);        } catch (ArithmeticException e) {           e.getMessage();        }    }}