package ExceptionHandling.FileException;import java.io.FileInputStream;import java.io.FileNotFoundException;import java.io.IOException;public class File {    public static void read(){        FileInputStream fs = null;        try {            fs = new FileInputStream("/Users/anujvidhate/Desktop/JavaAF/Java/src/ExceptionHandling/xd");            System.out.println("file is reading......");        } catch (FileNotFoundException e) {            System.out.println("file Not found");            e.fillInStackTrace();        } finally {            if (fs!=null) {                try {                    fs.close();                } catch (IOException e) {                    System.out.println("not close the file");                    e.printStackTrace();                }            }        }    }    public static void main(String[] args) {       read();    }}