package ExceptionHandling.InvalidAgeException;import java.util.Scanner;public class AgeMain {    public static void checkAge(int age) throws InvalidAge {        if (age < 0)            throw new InvalidAge("Bkl 0 age nahi hoTii sai se daal :)");        System.out.println("it is Valid age");    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.println("Enter you age mf- ");        int age = sc.nextInt();        try {            checkAge(age);        } catch (InvalidAge e) {           e.getMessage();        }    }}