package ExceptionHandling.LoginException;import java.util.Scanner;public class LoginMain {    public static void main(String[] args) {        Scanner scu=new Scanner(System.in);        System.out.println("Enter your user name");        String usname=scu.nextLine();        Scanner scp=new Scanner(System.in);        System.out.println("Enter your Password");        int usPas=scp.nextInt();        Credential cre=new Credential();        try {            cre.check(usname, usPas);        } catch (InvalidCradentialsError e) {            System.out.println(e.getMessage());        }    }}