package ExceptionHandling.NegativeNumberException;import java.util.Scanner;public class Factorial {    public static void calculate(int num)throws NegativeNumberException{        if(num<0)            throw new NegativeNumberException("negative no factorial cannot genrated");        int total=1;        for(int i=1;i<=num;i++){          total=total*i;        }        System.out.println(total);    }    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        System.out.println("Enter no");        int num= sc.nextInt();        try {            calculate(num);        }        catch (NegativeNumberException e) {            System.err.println(e.getMessage());        }    }}