package Inheritance.Eletronic_Device;public class Lcd extends Tv {    int size,money;    public Lcd(){        super();        size=0;        money=0;    }    public Lcd(String type,String company,int size,int money){        super(type,company);        this.size=size;        this.money=money;    }    @Override    public String toString() {        return super.toString()+" | Tv size- "+size+"cm"+" | Lcd Tv price is-"+money;    }}