package Inheritance.Eletronic_Device;public class Led extends Tv {    int size,money;    public Led(){        super();        size=0;        money=0;    }    public Led(String type,String company,int size,int money){        super(type,company);        this.size=size;        this.money=money;    }    @Override    public String toString() {        return super.toString()+" | Tv size- "+size+"cm"+" | Led Tv price is-"+money;    }}