package InterFace.Shorting;public class BubbleSort implements Sortable {        public void sort(int[] ar) {            int total=ar.length;            for (int i=0;i<total-1;i++) {                int min=i;                for (int j=0;j<total-i-1;j++) {                    if (ar[j]>ar[min]) {                     min=j;                    }                }                int temp = ar[min];                ar[min] = ar[i];                ar[i] = temp;            }        }    }