package InterFace.Shorting;public class SelectionSort implements Sortable {    public void sort(int arr[]) {        int total = arr.length;        int temp;        for (int i = 0; i < total; i++) {            int min = i;            for (int j = i + 1; j < total; j++) {                if (arr[min] > arr[j]) {                    min = j;                }              if(min!=i){                  temp=arr[i];                  arr[i]=arr[min];                  arr[min]=temp;              }            }        }    }}