package Labs.LabExercise_10_;public class Employee {    private int employeeId;    private String name;    private Date dateOfJoining;    public Employee(int employeeId, String name, Date dateOfJoining) {        this.employeeId = employeeId;        this.name = name;        this.dateOfJoining = dateOfJoining;    }    public int getEmployeeId() {        return employeeId;    }    public void setEmployeeId(int employeeId) {        this.employeeId = employeeId;    }    public String getName() {        return name;    }    public Date getDateOfJoining() {        return dateOfJoining;    }    public void setDateOfJoining(Date dateOfJoining) {        this.dateOfJoining = dateOfJoining;    }    public void displayDetails() {        System.out.println("Employee ID-" + employeeId+"Name: " + name+ "joining date- "+ dateOfJoining);    }}