package Labs.LabExercise_24;import java.util.ArrayList;import java.util.HashSet;import java.util.Scanner;public class EmployeeMain1 {    public static void main(String[] args) {        ArrayList<Employee> employeeList = new ArrayList<>();        HashSet<String> skillSet = new HashSet<>();        UtilityList<Employee> utilityList = new UtilityList<>();        UtilityReport utilityReport = new UtilityReport();        SortUtil<Employee> sortUtil = new SortUtil<>();        SearchUtil<Employee> searchUtil = new SearchUtil<>();        skillSet.add("C");        skillSet.add("C++");        skillSet.add("Java");        skillSet.add("HTML");        skillSet.add("R");        employeeList.add(new Employee(102, "Ram", 3456, skillSet));        employeeList.add(new Employee(101, "Mangesh", 23450, skillSet));        utilityList.printList(employeeList);        utilityReport.showReport(employeeList);        sortUtil.sort(employeeList);        utilityList.printList(employeeList);        sortUtil.sortBySalary(employeeList);        utilityList.printList(employeeList);        Scanner sc = new Scanner(System.in);        System.out.println("Enter Employee Id:");        int id = sc.nextInt();        try {            searchUtil.searchEmpInfo(id, employeeList);        } catch (EmployeeRecordNotFoundException e) {            System.err.println(e.getMessage());        }    }}