package Labs.LabExercise_24;import java.util.ArrayList;import java.util.List;class SearchUtil<T extends Employee> {    public void searchEmpInfo(int id, List<T> list) throws EmployeeRecordNotFoundException {        for (T employee : list) {            if (employee.id == id) {                System.out.println("Employee found: " + employee);                return;            }        }        throw new EmployeeRecordNotFoundException("Employee with ID " + id + " not found.");    }}