package Labs.LabExercise_25;public class Employee {    private int id;    private String name;    private double salary;    public Employee(int id,String nm,double sal){        this.id=id;        this.name=nm;        this.salary=sal;    }    public int getId() {        return id;    }    public String getName() {        return name;    }    public double getSalary() {        return salary;    }    @Override    public String toString() {        return "Employee Id- "+id+" Employee Name- "                                                  +name+"Salary- "+salary;    }}