package Labs.LabExercise_7;public class Employee {    private int empId;    private String empName;    private double bSal;    private double hra;    private double medical;    private double pf;    private double pt;    private double netSal;    private double gSal;    public Employee() {        empId = 101;        empName = "Anuj";        bSal = 110000.0;        hra = 90000.0;        medical = 8000.0;        pf = 80000.0;        pt = 200.0;        gSal = 900000.0;        netSal =100000.0;    }    public Employee(int empId , String empName, double bSal, double medical) {        this.empId = empId;        this.empName = empName;        this.bSal = bSal;        this.medical = medical;    }    public void setEmpId(int empId) {        this.empId = empId;    }    public int getEmpId() {        return empId;    }    public void setEmpName(String empName) {        this.empName = empName;    }    public String getEmpName() {        return empName;    }    public void setbSal(double bSal) {        this.bSal = bSal;    }    public double getbSal() {        return bSal;    }    public void setNetSal(double netSal) {        this.netSal = netSal;    }    public double getNetSal() {        return netSal;    }    public void setgSal(double gSal) {        this.gSal = gSal;    }    public double getgSal() {        return gSal;    }    private void calculateSal() {        hra = 0.5 * bSal;       pf = 0.12 * bSal;       gSal = bSal + hra + medical;        netSal = gSal - (pf + pt);    }    public String toString() {        return "Emp Id : "+empId+" | Emp Name : "+empName+" | Basic Salary : "+bSal ;    }}