package Labs_1.LabExercise_22;public class Account {    int accNO;    String ownerName;    double balance;    double withdraw;    public Account(){        accNO=23456;        ownerName="xd";        balance=3000;    }    public Account(int no,String nm,double bal){        this.accNO=no;        this.ownerName=nm;        this.balance=bal;    }    public void Withdraw(double withdraw)throws InsufficientBalance{        if((balance-withdraw)<1000)            throw new InsufficientBalance("YOU HAVE MINIMUM 1000 RS BALANCE REQURIED IN ACCOUNT GAREEB ");        System.out.println( "Total balance after withdraw is- " +(balance-withdraw));    }}