package Labs_1.LabExercise_24;import java.util.Collections;import java.util.Comparator;import java.util.List;class SortUtil<T extends Employee> implements Comparator<T> {    public void sort(List<T> list) {        Collections.sort(list);    }    public void sortBySalary(List<T> list) {        list.sort(new SortUtil<>());    }    @Override    public int compare(T o1, T o2) {        return Double.compare(o1.salary, o2.salary);    }}