package Labs_1.LabExercise_5;public class Date {    int day;    int month;    int year;    public Date(){        day =10;       month =11;        year=2021;    }    public Date(int day, int month){        this.day =10;        this.month =11;    }    public Date(int day,int month,int year){        this(day,month);        this.day=day;        this.month=month;        this.year=year;    }    public void printDate() {        System.out.println(day + "/" + month + "/" + year);    }}