package Input_output;import java.io.File;public class Problem_1 {public static void main(String[] args) {    String filePath = "/Users/anujvidhate/Desktop/JavaAF/Java/src/FILE1.txt";    File file = new File(filePath);    if (file.exists() && file.isFile()) {        System.out.println("File exists.");        System.out.println("File Information:");        System.out.println("Absolute Path: " + file.getAbsolutePath());        System.out.println("File Size: " + file.length() + " bytes");    } else {        System.out.println("File not found: " + filePath);    }}}