package Input_output;import java.io.File;import java.io.FileInputStream;import java.io.FileOutputStream;import java.io.IOException;public class Problem_2 {    public static void main(String[] args) throws IOException {        File f1= new File("/Users/anujvidhate/Desktop/JavaAF/Java/src/FILE1.txt");        File f2=new File("/Users/anujvidhate/Desktop/JavaAF/Java/src/File2.txt");        FileInputStream fin=new FileInputStream(f1);        FileOutputStream fout=new FileOutputStream(f2);        int x;        while((x=fin.read())!=-1) {            fout.write(x);        }        System.out.println("file Copied....");        fin.close();        fout.close();    }}