package Labs_2.accout;import java.io.Serializable;public class Account implements Serializable{    int accno;    String name;    double Bal;    public Account() {    }    public Account(int accno,String name,double  Bal) {        this.accno=accno;        this.name=name;        this.Bal=Bal;    }    public String toString() {        return "Accountno:-"+accno+"  "+"NAme:-"+name+"  "+"Balance:-"+Bal;    }    public void printDetails() {        System.out.println("AccountNo:-"+accno);        System.out.println("NAme:-"+name);        System.out.println("Balance:-"+Bal);    }}