package Lambda;public class LambdaMain {    public static void main(String[] args) {        Cube cb;        cb=(i)->{            System.out.println(i*i*i);        };        Length ln;        ln= (name) -> {            return name.length();        };        Maximum max;        max=(a,b)->{            if(a<b){                return b;            }else {                return a;            }        };        cb.cubeDisplay(2);      int total =  ln.LengthDisplay("zoro");       int maxNum= max.maximumDisplay(3,7);        System.out.println("Total lenth "+total);        System.out.println("Maximum no is "+maxNum);    }}